import React, { Component } from 'react'


export default class OnePage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            oneRes: {
                input: [],
                sum: 0,
            },
            twoRes: {
                input: '',
                res: 0
            }
        }
    }

    randomInput = () => {
        const n = this.randomNumber();
        let arr = [];
        for (let index = 0; index < n; index++) {
            arr.push(this.randomNumber());
        }
        return arr;
    }
    randomNumber = () => {
        return Math.floor(Math.random() * 20);
    }

    fibonaci(valueInput) {
        if (valueInput === 1 || valueInput === 2) {
            return 1;
        }
        return this.fibonaci(valueInput - 1) + this.fibonaci(valueInput - 2);

    }

    handleChangeTex = (event) => {
        console.log(event.target.value);
        const {twoRes } =this.state;
        console.log(twoRes);
        this.setState({
            ...this.state,
            twoRes: {
                ...twoRes,
                input: event.target.value
            }
        })
    }

    resultQuestionOne = ()=> {
        const input = this.randomInput();
        const sum = input.reduce((acc, current) => acc + current, 0);
        const oneRes = {
            input,
            sum
        }
        this.setState({
            oneRes: oneRes
        })
    }
    resultQuestionTwo = ()=> {
        const input = this.randomInput();
        const sum = input.reduce((acc, current) => acc + current, 0);
        const oneRes = {
            input,
            sum
        }
        this.setState({
            oneRes: oneRes
        })
    }
    render() {
        const { oneRes,twoRes } = this.state;
        return (
            <div>
                <div>
                    <h1> QUESTION 1: </h1>
                    <h2>Input random array:</h2>
                    <div>
                        <h2 style={{ color: 'blue' }}>
                            {oneRes.input.toString()}
                        </h2>
                    </div>
                    <button onClick={this.resultQuestionOne}>View Result</button>
                    <h2 style={{ color: 'red' }}>
                        {oneRes.sum.toString()}
                    </h2>
                </div>
                <div>
                    <h1> QUESTION 2: </h1>
                    <input
                        type="number"
                        placeholder="please !"
                        value={this.state.valueSearch}
                        onChange={this.handleChangeTex} />

                    <button onClick={this.resultQuestionTwo}> View Result </button>

                    <h2 style={{ color: 'red' }}>
                        {twoRes.res.toString()}
                    </h2>
                </div>
            </div>

        )
    }
}
