import React, { Component } from 'react'


export default class OnePage extends Component {
    constructor(props) {
        super(props);
        this.state = {
          oneRes: {
            input: [],
            sum:  0,
          }

        }
      }
      randomInput = () => {
        const n = this.randomNumber();
        let arr = [];
        for (let index = 0; index < n; index++) {
          arr.push(this.randomNumber());
        }
        return arr;
      }
      randomNumber = () => {
        return Math.floor(Math.random() * 20);
      }
      resultQuestionOne(){
        const input = this.randomInput();
        const sum = input.reduce((acc, current) => acc + current, 0);
        const oneRes = {
            input,
            sum
        }
        this.setState({
            oneRes: oneRes
        })
      }
      render() {
        const { oneRes } = this.state;
        return (
          <div>
            <h1> QUESTION 1: </h1>
            <h2>
              Input random array: 
            </h2>
            <div>
                <h2 style = {{color: 'red'}}>

                </h2>
            </div>
            <button onClick={() => this.resultQuestionOne()}>
                Result One
            </button>
            <h2>
                {oneRes.sum.toString()}
            </h2>
          </div>
        )
      }
}
