import React, { Component } from 'react'
import SearchComponent from '../components/SearchComponent';
import MainComponent from '../components/MainComponent';
import ListComponent from '../components/ListComponent';


export default class YoutubePage extends Component {

  state = {
    listVideo: DATA,
    selectedVideo: DATA[0]
  }
  onSearch = (data) => {
    const listVideo = DATA.filter(x => x.name.includes(data))


    this.setState({
      listVideo: listVideo
    })
  }
  onSelect = (videoId) => {
    console.log(videoId);

      let selectedVideo = DATA.find(x=> x.videoId === videoId);
      if(selectedVideo) {
        this.setState({
          selectedVideo: selectedVideo
        })
      }
  }
  render() {
    return (
      <div className="frameBody">
        <div align="center">
          <SearchComponent onSearch={this.onSearch} />
        </div>

        <div className="frameMainList">
          <div className="frameMain">
            <MainComponent item = {this.state.selectedVideo} />
          </div>
          <div className="frameList">
            <ListComponent listVideo={this.state.listVideo} onSelect = {this.onSelect}/>
          </div>
        </div>
      </div>
    )

  }
}

const DATA = [
  {
    id: 1,
    name: 'Cho tui xin một vé đi tuổi thơ!',
    videoId: 'I0y45gN36Vk',
  },
  {
    id: 2,
    name: 'Nhạc không lời tập trung học tập',
    videoId: '4P0R9dUJ370',
  },
  {
    id: 3,
    name: 'Cho tui xin một vé đi tuổi thơ!',
    videoId: 'I0y45gN36Vk',
  },
  {
    id: 4,
    name: 'Cho tui xin một vé đi tuổi thơ!',
    videoId: 'I0y45gN36Vk',
  },
  {
    id: 5,
    name: 'Nhạc không lời tập trung học tập',
    videoId: '4P0R9dUJ370',
  },
  {
    id: 6,
    name: 'Cho tui xin một vé đi tuổi thơ!',
    videoId: 'I0y45gN36Vk',
  },
]
