import React, { Component } from 'react'


export default class LevelTwoOnePage extends Component {

  constructor(props) {
    super(props);
    this.state = {
      listCriteria: [],
      listIndices: [],
      inputOne: '',
      resOne: null,
      inputTwo: '',
      resTwo: []
    }
  }
  async componentWillMount() {
    await this.loadData();
  }
  loadData = async () => {
    try {
      const options = {
        'method': 'GET',
        'Content-Type': 'application/json'
      }
      let res = await fetch('https://xbot.com.vn/standards.json', options)
      if (res) {
        let listResult = await res.json();
        let listCriteria = [];
        let listIndices = [];
        listResult.map(item => {
          let tempList = item.criteria;
          listCriteria = listCriteria.concat(tempList);
          tempList.map(criteria => {
            listIndices = listIndices.concat(criteria.indices);
          })
        });


        this.setState({
          listCriteria: listCriteria,
          listIndices: listIndices
        })
      }
    } catch (error) {
      console.log(error);

    }
  }
  handleChangeTexOne = (event) => {
    this.setState({
      inputOne: event.target.value
    })
  }
  handleChangeTexTwo = (event) => {
    this.setState({
      inputOne: event.target.value
    })
  }

  resultQuestionOne = () => {
    const { listCriteria, inputOne } = this.state;


    let result = listCriteria.find(x => x.name.toString().includes(inputOne));
    console.log(result);
    this.setState({
      resOne: result
    })
  }
  resultQuestionTwo = () => {
    const { inputTwo,listIndices } = this.state;
    if(inputTwo === '') {
      this.setState( {
        resTwo: listIndices
      })
    } else {
      if(isNaN(inputTwo)) {
        alert('Error input, p')
      }
      this.setState( {
        resTwo: listIndices.filter(x=> x.level === inputTwo)
      })
    }
  }
  render() {
    return (
      <div>
        <div>
          <h1> QUESTION 1: </h1>
          <input
            type="text"
            placeholder="search"
            value={this.state.inputOne}
            onChange={this.handleChangeTexOne} />
          <button onClick={this.resultQuestionOne}> View Result </button>

          <h2 style={{ color: 'red' }}>
            Result
          </h2>
          {this.state.resOne &&
            (
              <div>
                <h5>  {this.state.resOne.key} </h5>
                <h5> {this.state.resOne.name} </h5>
              </div>
            )
          }
        </div>

         <div>
          <h1> QUESTION 2: </h1>
          <input
            type="number"
            placeholder="level"
            value={this.state.inputTwo}
            onChange={this.handleChangeTexTwo} />
          <button onClick={this.resultQuestionTwo}> View Result </button>

          <h2 style={{ color: 'red' }}>
            Result
          </h2>
          {this.state.resOne &&
            (
              <div>
                <h5>  {this.state.resOne.key} </h5>
                <h5> {this.state.resOne.name} </h5>
              </div>
            )
          }
        </div>
      </div>
    )

  }
}
