import React, { Component } from 'react'


export default class OnePage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            oneRes: {
                input: [],
                sum: 0,
            }
        }
    }

    randomInput = () => {
        const n = this.randomNumber();
        let arr = [];
        for (let index = 0; index < n; index++) {
            arr.push(this.randomNumber());
        }
        return arr;
    }
    randomNumber = () => {
        return Math.floor(Math.random() * 20);
    }

    fibonaci(valueInput) {
        if (valueInput === 1 || valueInput === 2) {
            return 1;
        }
        return this.fibonaci(valueInput - 1) + this.fibonaci(valueInput - 2);

    }

    handleChangeTextOne = (event) => {

    }

    resultQuestionOne = ()=> {
        const input = this.randomInput();
        const sum = input.reduce((acc, current) => acc + current, 0);
        const oneRes = {
            input,
            sum
        }
        this.setState({
            oneRes: oneRes
        })
    }
    render() {
        const { oneRes } = this.state;
        return (
            <div>
                <div>
                    <h1> QUESTION 1: </h1>
                    <h2>Input random array:</h2>
                    <div>
                        <h2 style={{ color: 'blue' }}>
                            {oneRes.input.toString()}
                        </h2>
                    </div>
                    <button onClick={() => this.resultQuestionOne}>Result One</button>
                    <h2 style={{ color: 'red' }}>
                        {oneRes.sum.toString()}
                    </h2>
                </div>
                <div>
                    <input
                        type="number"
                        placeholder="please !"
                        value={this.state.valueSearch}
                        onChange={this.handleChangeTextOne} />

                    <button onClick={() => this.handleChangeTextOne()}> Submit </button>
                </div>
            </div>

        )
    }
}
