import React, { Component } from 'react'
import YouTube from 'react-youtube'
const styles = { flexDirection: 'row', display: 'flex', marginTop: 10 };
export default class ListComponent extends Component {

    opts = {
        height: '390',
        width: '640',
        playerVars: {
            autoplay: 0
        }
    };

    onStateChange = async (event) => {
        let videoData =  await event.target.getVideoData();
        console.log(videoData.video_id);

        if(videoData) {
            return
        }

    }
    render() {
        return (
            <div className="frameContentList">
                {this.props.listVideo.map(item =>
                    // eslint-disable-next-line jsx-a11y/anchor-is-valid
                    <a key={item.id} style={styles} >
                        <YouTube
                            opts ={this.opts}
                            onStateChange = { this.onStateChange}
                            videoId={item.videoId}
                            className="videoList"
                        />
                        <p className="frameName">{item.name}</p>
                    </a>
                )}
            </div>
        )
    }
}
